{% assign selling_plan = product.metafields.carlos.selling_plan %}
<script>
  $(document).ready(function() {
  var checkExist = setInterval(function() {
    if (document.querySelector('.add-to-cart-container .selling-plan-fieldset')) {
      clearInterval(checkExist);
      membership();
    }
  }, 100);

  function membership() {
    var CMID = $('.selling-plan-fieldset').attr('data-id');
    {% if selling_plan == "m" %}
      var monthly = "{{ product.selling_plan_groups[0].selling_plans[0].id }}";
      $(".add-to-cart-container .selling-plan-fieldset .selling-plan-label[for='selling_plan_" + CMID + "_" + monthly +"']").click();
    {% endif %}
      {% if selling_plan == "yr" %}
        var yearly = "{{ product.selling_plan_groups[0].selling_plans[1].id }}";
        $(".add-to-cart-container .selling-plan-fieldset .selling-plan-label[for='selling_plan_" + CMID + "_" + yearly +"']").click();
    {% endif %}

    $('.cta-btn, .main-img-btn, .membership-btn').click(function() {
      $('#addToCartButton').click();
    });
  }
});
</script>
